"use strict";angular.module("sbAdminApp").controller("AdvertCtrl",["$scope","$state","$timeout","Advertiser","$sce","Campaign",function($scope,$state,$timeout,Advertiser,$sce,Camapign){function drawGraph(calVal){var a=new Date(calVal.last),b=new Date(calVal.curDate);Advertiser.getGraphdata(a.getTime(),b.getTime()).then(function(res){var retVal=res.data;if(retVal.success){var graphData=[],totalSpent=0;angular.forEach(retVal.daily,function(val){graphData.push([val.dtStamp,val.amountSpent]),totalSpent+=val.amountSpent});var delayDays=Math.round((b-a)/864e5);$scope.delayDays=0==delayDays?1:delayDays+1,$scope.totalSpent=totalSpent,$scope.myJson={type:"bar",utc:!0,"scale-x":{"min-value":graphData[0][0],step:"day",transform:{type:"date",all:"%d, %M"}},"scale-y":{label:{text:"INR (Rs) Spent"}},series:[{values:graphData}]}}})}function showCampaign(){Camapign.getAll().then(function(res){var retVal=res.data;retVal.success&&($scope.campaigns=retVal.campaigns)})}function sorting(){var seed=$scope.sortSeed,tempArr=$scope.campaigns,retArr=[];angular.forEach(tempArr,function(row){var temp=row[seed];console.log(temp),temp.indexOf(seed)>=0&&retArr.push(row)}),$scope.compaigns=retArr}var userInfo=null;$scope.begin_date=null,$scope.end_date=null,$scope.popoverFlag=!1,$scope.showItemIcon=[!1,!1,!1,!1,!1];var seedInd=0;$scope.init=function(){showCampaign(),Advertiser.getUserInfo().then(function(res){res.data.success?(userInfo=res.data.advertiser,$scope.user={name:userInfo.userName,advertiserId:userInfo.advertiserId,balance:userInfo.currency+" "+userInfo.balance,totalLiveCampaign:userInfo.totalLiveCampaign}):$state.go("login")},function(err){$state.go("login")}),$scope.calCallback=drawGraph,$scope.selSort(0)},$scope.s_info={myInterval:"5000",noWrapSlides:!1,active:0},$scope.s_info.slides=[{text:"hell",image:"http://lorempixel.com/400/200/food",id:0},{text:"sadas",image:"http://lorempixel.com/400/200/people",id:1}],$scope.createCampaign=function(){$state.go("campaigncreate.create")},$scope.showDate=function(timeVal){return moment(timeVal).format("MMM DD, YYYY")},$scope.selSort=function(ind){seedInd=ind,$scope.showSortItem=[!1,!1,!1,!1,!1,!1],$scope.showSortItem[ind]=!0,$scope.sortPopoverFlag=!1,sorting()},$scope.$watch("sortSeed",function(val){sorting()})}]);