"use strict";angular.module("sbAdminApp").controller("AdvertCtrl",["$scope","$state","$timeout","Advertiser","$sce","Campaign",function($scope,$state,$timeout,Advertiser,$sce,Camapign){function drawGraph(){var a=new Date(last),b=new Date(curDate);Advertiser.getGraphdata(a.getTime(),b.getTime()).then(function(res){var retVal=res.data;if(retVal.success){var graphData=[],totalSpent=0;angular.forEach(retVal.daily,function(val){graphData.push([val.dtStamp,val.amountSpent]),totalSpent+=val.amountSpent});var delayDays=Math.round((b-a)/864e5);$scope.delayDays=0==delayDays?1:delayDays+1,$scope.totalSpent=totalSpent,$scope.myJson={type:"bar",utc:!0,"scale-x":{"min-value":graphData[0][0],step:"day",transform:{type:"date",all:"%d, %M"}},"scale-y":{label:{text:"INR (Rs) Spent"}},series:[{values:graphData}]}}})}function showCalendarValue(){$scope.searchDate=last.format("MMM DD, YYYY")+" - "+curDate.format("MMM DD, YYYY"),drawGraph()}function showCampaign(){Camapign.getAll().then(function(res){var retVal=res.data;retVal.success&&($scope.campaigns=retVal.campaigns)})}var userInfo=null;$scope.begin_date=null,$scope.end_date=null,$scope.popoverFlag=!1,$scope.showItemIcon=[!1,!1,!1,!1,!1];var curDate=null,last=null,selDateRange=2;$scope.init=function(){showCampaign(),Advertiser.getUserInfo().then(function(res){res.data.success?(userInfo=res.data.advertiser,$scope.user={name:userInfo.userName,advertiserId:userInfo.advertiserId,balance:userInfo.currency+" "+userInfo.balance,totalLiveCampaign:userInfo.totalLiveCampaign}):$state.go("login")},function(err){$state.go("login")}),$scope.selRange(1),showCalendarValue()},$scope.selRange=function(ind){switch(selDateRange=ind,curDate=moment(),last=null,$scope.showItemIcon=[!1,!1,!1,!1,!1],$scope.showItemIcon[ind]=!0,ind){case 0:last=moment().date(1);break;case 1:last=curDate.clone().subtract(30,"d");break;case 2:last=moment().subtract(6,"d");break;case 3:last=moment().subtract(1,"d");break;default:last=moment()}var min1=last.toDate(),min2=min1,max1=curDate.toDate(),max2=max1;moment(curDate).month()!==moment(last).month()&&(max1=last.endOf("month").toDate(),min2=curDate.clone().date(1).toDate()),$scope.cal1options={minDate:min1,maxDate:max1,initDate:moment().subtract(1,"months").toDate(),maxMode:"day",showWeeks:!1},$scope.cal2options={minDate:min2,maxDate:max2,initDate:moment().toDate(),maxMode:"day",showWeeks:!1}},$scope.closeCalendar=function(flag){flag&&showCalendarValue(),$scope.popoverFlag=!1},$scope.s_info={myInterval:"5000",noWrapSlides:!1,active:0},$scope.s_info.slides=[{text:"hell",image:"http://lorempixel.com/400/200/food",id:0},{text:"sadas",image:"http://lorempixel.com/400/200/people",id:1}],$scope.showDate=function(timeVal){return moment(timeVal).format("MMM DD, YYYY")}}]);