"use strict";angular.module("sbAdminApp").controller("LoginCtrl",function($scope,Auth,$state){$scope.errflag=!1,$scope.errmsg="",$scope.login=function(){Auth.login({userName:$scope.userName,password:$scope.password}).then(function(res){var retVal=res.data;retVal.success?(Auth.authentication(retVal.token),$state.go("dashboard.advert")):($scope.errflag=!0,$scope.errmsg=retVal.message)})}});