"use strict";angular.module("sbAdminApp").controller("SettingCtrl",["$scope","$timeout","Advertiser","toastr",function($scope,$timeout,Advertiser,toastr){$scope.save=function(setting){Advertiser.putBilling({address:setting}).then(function(res){res.data.success?toastr.success(res.data.message,"Saving Status",{positionClass:"toast-top-center",timeOut:5e3,isOpened:!0}):toastr.error(res.data.message,"Saving Error",{positionClass:"toast-top-center",timeOut:5e3,isOpened:!0})})}}]);